import React from 'react';
import styled from 'styled-components';

const AboutSection = styled.section`
  padding: 6rem 2rem;
  background-color: ${props => props.theme.colors.background};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  
  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
    text-align: center;
  }
`;

const Content = styled.div``;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 2.5rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 1.5rem;
`;

const Description = styled.p`
  font-family: ${props => props.theme.fonts.secondary};
  font-size: 1.1rem;
  color: ${props => props.theme.colors.text};
  line-height: 1.8;
  margin-bottom: 2rem;
`;

const Stats = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  margin-top: 3rem;
  
  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
  }
`;

const StatItem = styled.div`
  text-align: center;
`;

const StatNumber = styled.div`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 2.5rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  font-family: ${props => props.theme.fonts.secondary};
  color: ${props => props.theme.colors.text};
`;

const ImageContainer = styled.div`
  img {
    width: 100%;
    height: auto;
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  }
`;

const About = () => {
  return (
    <AboutSection id="a-propos">
      <Container>
        <Content>
          <Title>À Propos de Nous</Title>
          <Description>
            Chez Fabulous, nous sommes passionnés par la création d'expériences numériques exceptionnelles. 
            Notre équipe d'experts combine créativité et expertise technique pour donner vie à vos projets.
            Nous croyons en l'innovation, la qualité et l'excellence dans tout ce que nous faisons.
          </Description>
          <Stats>
            <StatItem>
              <StatNumber>100+</StatNumber>
              <StatLabel>Projets Réalisés</StatLabel>
            </StatItem>
            <StatItem>
              <StatNumber>50+</StatNumber>
              <StatLabel>Clients Satisfaits</StatLabel>
            </StatItem>
            <StatItem>
              <StatNumber>5+</StatNumber>
              <StatLabel>Années d'Expérience</StatLabel>
            </StatItem>
          </Stats>
        </Content>
        <ImageContainer>
          <img src="/images/about-image.jpg" alt="Notre équipe au travail" />
        </ImageContainer>
      </Container>
    </AboutSection>
  );
};

export default About;
