import React, { useState } from 'react';
import styled from 'styled-components';

const AppointmentSection = styled.section`
	padding: 4rem 2rem;
	background: rgba(255, 255, 255, 0.9);
`;

const AppointmentForm = styled.form`
	max-width: 600px;
	margin: 0 auto;
	display: flex;
	flex-direction: column;
	gap: 1rem;
`;

const FormGroup = styled.div`
	display: flex;
	flex-direction: column;
	gap: 0.5rem;
`;

const Input = styled.input`
	padding: 0.5rem;
	border: 1px solid #ccc;
	border-radius: 4px;
`;

const Select = styled.select`
	padding: 0.5rem;
	border: 1px solid #ccc;
	border-radius: 4px;
`;

const Button = styled.button`
	padding: 1rem 2rem;
	background: #D4AF37;
	color: white;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	&:hover {
		background: #B4941F;
	}
`;

function Appointment() {
	const [formData, setFormData] = useState({
		name: '',
		email: '',
		phone: '',
		date: '',
		time: '',
		projectType: '',
		message: ''
	});

	const [availableSlots, setAvailableSlots] = useState([]);
	const [message, setMessage] = useState('');

	const handleDateChange = async (e) => {
		const selectedDate = e.target.value;
		setFormData({ ...formData, date: selectedDate });
		try {
			const response = await fetch(`http://localhost:5001/api/available-slots?date=${selectedDate}`);
			const data = await response.json();
			setAvailableSlots(data.availableSlots);
		} catch (error) {
			console.error('Erreur lors de la récupération des créneaux:', error);
		}
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const response = await fetch('http://localhost:5001/api/appointment', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
				},
				body: JSON.stringify(formData),
			});
			const data = await response.json();
			if (response.ok) {
				setMessage('Rendez-vous créé avec succès !');
				setFormData({
					name: '',
					email: '',
					phone: '',
					date: '',
					time: '',
					projectType: '',
					message: ''
				});
			} else {
				setMessage(data.error || 'Une erreur est survenue');
			}
		} catch (error) {
			setMessage('Erreur lors de la création du rendez-vous');
		}
	};

	return (
		<AppointmentSection id="appointment">
			<h2>Prendre Rendez-vous</h2>
			{message && <p>{message}</p>}
			<AppointmentForm onSubmit={handleSubmit}>
				<FormGroup>
					<label>Nom</label>
					<Input
						type="text"
						value={formData.name}
						onChange={(e) => setFormData({ ...formData, name: e.target.value })}
						required
					/>
				</FormGroup>
				<FormGroup>
					<label>Email</label>
					<Input
						type="email"
						value={formData.email}
						onChange={(e) => setFormData({ ...formData, email: e.target.value })}
						required
					/>
				</FormGroup>
				<FormGroup>
					<label>Téléphone</label>
					<Input
						type="tel"
						value={formData.phone}
						onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
						required
					/>
				</FormGroup>
				<FormGroup>
					<label>Date</label>
					<Input
						type="date"
						value={formData.date}
						onChange={handleDateChange}
						required
					/>
				</FormGroup>
				<FormGroup>
					<label>Heure</label>
					<Select
						value={formData.time}
						onChange={(e) => setFormData({ ...formData, time: e.target.value })}
						required
					>
						<option value="">Sélectionnez une heure</option>
						{availableSlots.map((slot) => (
							<option key={slot} value={slot}>{slot}</option>
						))}
					</Select>
				</FormGroup>
				<FormGroup>
					<label>Type de Projet</label>
					<Select
						value={formData.projectType}
						onChange={(e) => setFormData({ ...formData, projectType: e.target.value })}
						required
					>
						<option value="">Sélectionnez un type</option>
						<option value="Résidentiel">Résidentiel</option>
						<option value="Commercial">Commercial</option>
						<option value="Bureau">Bureau</option>
						<option value="Autre">Autre</option>
					</Select>
				</FormGroup>
				<FormGroup>
					<label>Message</label>
					<textarea
						value={formData.message}
						onChange={(e) => setFormData({ ...formData, message: e.target.value })}
						required
						rows="4"
					/>
				</FormGroup>
				<Button type="submit">Prendre Rendez-vous</Button>
			</AppointmentForm>
		</AppointmentSection>
	);
}

export default Appointment;
