import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faQuoteLeft } from '@fortawesome/free-solid-svg-icons';

const TestimonialsSection = styled.section`
  padding: 6rem 2rem;
  background-color: ${props => props.theme.colors.background};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 2.5rem;
  color: ${props => props.theme.colors.primary};
  text-align: center;
  margin-bottom: 3rem;
`;

const TestimonialsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
`;

const TestimonialCard = styled.div`
  background-color: white;
  padding: 2rem;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const QuoteIcon = styled.div`
  color: ${props => props.theme.colors.primary};
  font-size: 2rem;
  margin-bottom: 1.5rem;
`;

const TestimonialText = styled.p`
  font-family: ${props => props.theme.fonts.secondary};
  font-size: 1.1rem;
  color: ${props => props.theme.colors.text};
  line-height: 1.8;
  margin-bottom: 1.5rem;
  font-style: italic;
`;

const ClientInfo = styled.div`
  margin-top: 2rem;
`;

const ClientImage = styled.img`
  width: 80px;
  height: 80px;
  border-radius: 50%;
  margin-bottom: 1rem;
  object-fit: cover;
`;

const ClientName = styled.h4`
  font-family: ${props => props.theme.fonts.primary};
  color: ${props => props.theme.colors.primary};
  margin-bottom: 0.5rem;
`;

const ClientRole = styled.p`
  font-family: ${props => props.theme.fonts.secondary};
  color: ${props => props.theme.colors.text};
  font-size: 0.9rem;
`;

const testimonials = [
  {
    id: 1,
    text: "L'équipe de Fabulous a transformé notre vision en réalité. Leur expertise et leur professionnalisme ont dépassé nos attentes.",
    name: "Sophie Martin",
    role: "CEO, TechStart",
    image: "/images/testimonials/client1.jpg"
  },
  {
    id: 2,
    text: "Un travail exceptionnel ! Le design est moderne et l'expérience utilisateur est parfaitement optimisée.",
    name: "Thomas Dubois",
    role: "Directeur Marketing, InnovateCorp",
    image: "/images/testimonials/client2.jpg"
  },
  {
    id: 3,
    text: "Je recommande vivement Fabulous pour leur créativité et leur capacité à livrer des projets de qualité dans les délais.",
    name: "Marie Laurent",
    role: "Fondatrice, StyleHub",
    image: "/images/testimonials/client3.jpg"
  }
];

const Testimonials = () => {
  return (
    <TestimonialsSection id="temoignages">
      <Container>
        <Title>Témoignages Clients</Title>
        <TestimonialsGrid>
          {testimonials.map(testimonial => (
            <TestimonialCard key={testimonial.id}>
              <QuoteIcon>
                <FontAwesomeIcon icon={faQuoteLeft} />
              </QuoteIcon>
              <TestimonialText>{testimonial.text}</TestimonialText>
              <ClientInfo>
                <ClientImage src={testimonial.image} alt={testimonial.name} />
                <ClientName>{testimonial.name}</ClientName>
                <ClientRole>{testimonial.role}</ClientRole>
              </ClientInfo>
            </TestimonialCard>
          ))}
        </TestimonialsGrid>
      </Container>
    </TestimonialsSection>
  );
};

export default Testimonials;
