function Appointment() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    date: '',
    time: '',
    message: ''
  });

  const [availableSlots, setAvailableSlots] = useState([]);
  const [loadingSlots, setLoadingSlots] = useState(false);
  const [status, setStatus] = useState({
    submitting: false,
    message: '',
    success: false
  });

  const loadAvailableSlots = async (selectedDate) => {
    if (!selectedDate) return;

    setLoadingSlots(true);
    setFormData(prev => ({ ...prev, time: '' }));

    try {
      const response = await fetch(`${API_URL}/api/available-slots?date=${selectedDate}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        throw new Error(`Erreur serveur: ${response.status}`);
      }

      const data = await response.json();
      console.log('Réponse reçue:', data);

      if (data.error) {
        throw new Error(data.error);
      }

      // Générer tous les créneaux possibles
      const allSlots = [];
      for (let hour = 9; hour <= 18; hour++) {
        for (let minute of ['00', '30']) {
          if (hour === 18 && minute === '30') continue;
          const time = `${hour.toString().padStart(2, '0')}:${minute}`;
          allSlots.push({
            time,
            available: data.availableSlots.includes(time)
          });
        }
      }

      setAvailableSlots(allSlots);

    } catch (error) {
      console.error('Erreur lors du chargement des créneaux:', error);
      setAvailableSlots([]);
    } finally {
      setLoadingSlots(false);
    }
  };

  const handleTimeSelect = (time) => {
    setFormData(prev => ({
      ...prev,
      time
    }));
  };

  useEffect(() => {
    if (formData.date) {
      loadAvailableSlots(formData.date);
    }
  }, [formData.date]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus({ submitting: true, message: '', success: false });

    try {
      // Toujours simuler une réponse réussie pour le moment
      setTimeout(() => {
        setStatus({
          submitting: false,
          message: 'Votre demande de rendez-vous a été envoyée avec succès ! Vous allez recevoir un email de confirmation.',
          success: true
        });

        setFormData({
          name: '',
          email: '',
          phone: '',
          date: '',
          time: '',
          message: ''
        });
      }, 1000);

    } catch (error) {
      console.error('Erreur lors de l\'envoi du formulaire:', error);
      setStatus({
        submitting: false,
        message: 'Une erreur est survenue. Veuillez réessayer plus tard.',
        success: false
      });
    }
  };

  return (
    <AppointmentSection id="appointment">
      <Container>
        <Title>Prendre Rendez-vous</Title>
        <Subtitle>
          Réservez votre moment de bien-être dès maintenant
        </Subtitle>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label>
              <IconWrapper>
                <FontAwesomeIcon icon={faUser} />
              </IconWrapper>
              Nom
            </Label>
            <Input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>
              <IconWrapper>
                <FontAwesomeIcon icon={faEnvelope} />
              </IconWrapper>
              Email
            </Label>
            <Input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>
              <IconWrapper>
                <FontAwesomeIcon icon={faPhone} />
              </IconWrapper>
              Téléphone
            </Label>
            <Input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>
              <IconWrapper>
                <FontAwesomeIcon icon={faCalendar} />
              </IconWrapper>
              Date
            </Label>
            <Input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
              min={new Date().toISOString().split('T')[0]}
            />
          </FormGroup>

          <FormGroup>
            <TimeSlotSection>
              <TimeSlotHeader>
                <TimeSlotTitle>
                  <IconWrapper>
                    <FontAwesomeIcon icon={faClock} />
                  </IconWrapper>
                  Heure souhaitée
                </TimeSlotTitle>
                <TimeSlotLegend>
                  <LegendItem>
                    <LegendDot $color="#D4AF37" />
                    Sélectionné
                  </LegendItem>
                  <LegendItem>
                    <LegendDot $color="white" style={{ border: '1px solid #ddd' }} />
                    Disponible
                  </LegendItem>
                  <LegendItem>
                    <LegendDot $color="#f0f0f0" />
                    Indisponible
                  </LegendItem>
                </TimeSlotLegend>
              </TimeSlotHeader>

              {loadingSlots ? (
                <div style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
                  <FontAwesomeIcon icon={faSpinner} spin /> Chargement des créneaux...
                </div>
              ) : !formData.date ? (
                <div style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
                  Veuillez d'abord choisir une date
                </div>
              ) : (
                <TimeSlotGrid>
                  {availableSlots.map(({ time, available }) => (
                    <TimeSlotButton
                      key={time}
                      type="button"
                      onClick={() => available && handleTimeSelect(time)}
                      $isAvailable={available}
                      $isSelected={formData.time === time}
                      title={available ? 'Cliquez pour sélectionner ce créneau' : 'Ce créneau n\'est pas disponible'}
                    >
                      {time}
                    </TimeSlotButton>
                  ))}
                </TimeSlotGrid>
              )}
            </TimeSlotSection>
          </FormGroup>

          <FormGroup>
            <Label>
              <IconWrapper>
                <FontAwesomeIcon icon={faComment} />
              </IconWrapper>
              Message
            </Label>
            <TextArea
              name="message"
              value={formData.message}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <SubmitButton 
            type="submit" 
            disabled={status.submitting}
          >
            {status.submitting ? 'Envoi en cours...' : 'Prendre Rendez-vous'}
          </SubmitButton>

          {status.message && (
            <Message $success={status.success}>
              {status.message}
            </Message>
          )}
        </Form>
      </Container>
    </AppointmentSection>
  );
}

export default Appointment;
