const Message = styled.div`
  text-align: center;
  margin-top: 1rem;
  padding: 1rem;
  border-radius: 5px;
  color: ${props => props.$success ? '#4CAF50' : '#f44336'};
  background: ${props => props.$success ? 'rgba(76, 175, 80, 0.1)' : 'rgba(244, 67, 54, 0.1)'};
  border: 1px solid ${props => props.$success ? '#4CAF50' : '#f44336'};
`;

const AddReviewForm = styled(Form)`
  margin-top: 2rem;
`;

const EmailVerification = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const EmailInput = styled(Input)`
  max-width: 400px;
  margin: 0 auto;
`;

const VerifyButton = styled(SubmitButton)`
  max-width: 200px;
  margin: 1rem auto;
`;

const API_URL = 'http://localhost:5000';

function Testimonials() {
  const [reviews, setReviews] = useState([]);
  const [emailVerification, setEmailVerification] = useState({
    email: '',
    verified: false,
    checking: false,
    error: ''
  });
  const [formData, setFormData] = useState({
    name: '',
    rating: 0,
    comment: '',
    email: ''
  });
  const [status, setStatus] = useState({
    submitting: false,
    message: '',
    success: false
  });

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    pauseOnHover: true,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      const response = await fetch(`${API_URL}/api/reviews`);
      const data = await response.json();
      setReviews(data);
    } catch (error) {
      console.error('Erreur lors de la récupération des avis:', error);
    }
  };

  const verifyEmail = async (e) => {
    e.preventDefault();
    setEmailVerification(prev => ({ ...prev, checking: true, error: '' }));

    try {
      const response = await fetch(`${API_URL}/api/appointments/verify-email`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: emailVerification.email })
      });

      const data = await response.json();

      if (response.ok) {
        setEmailVerification(prev => ({
          ...prev,
          verified: true,
          checking: false,
          error: ''
        }));
        setFormData(prev => ({
          ...prev,
          email: emailVerification.email,
          name: data.name || ''
        }));
      } else {
        setEmailVerification(prev => ({
          ...prev,
          verified: false,
          checking: false,
          error: data.message || "Une erreur s'est produite"
        }));
      }
    } catch (error) {
      setEmailVerification(prev => ({
        ...prev,
        verified: false,
        checking: false,
        error: "Une erreur s'est produite lors de la vérification"
      }));
    }
  };

  const handleEmailChange = (e) => {
    setEmailVerification(prev => ({
      ...prev,
      email: e.target.value,
      verified: false,
      error: ''
    }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRatingClick = (rating) => {
    setFormData(prev => ({
      ...prev,
      rating
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus({ submitting: true, message: '', success: false });

    try {
      const response = await fetch(`${API_URL}/api/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (response.ok) {
        setStatus({
          submitting: false,
          message: 'Votre avis a été ajouté avec succès !',
          success: true
        });
        setFormData({
          name: '',
          rating: 0,
          comment: '',
          email: ''
        });
        setEmailVerification({
          email: '',
          verified: false,
          checking: false,
          error: ''
        });
        fetchReviews();
      } else {
        setStatus({
          submitting: false,
          message: data.message || "Une erreur s'est produite",
          success: false
        });
      }
    } catch (error) {
      setStatus({
        submitting: false,
        message: "Une erreur s'est produite lors de l'envoi",
        success: false
      });
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  return (
    <TestimonialsSection id="testimonials">
      <Container>
        <SectionTitle>
          <h2>Témoignages</h2>
          <p>Découvrez ce que nos clients pensent de nos services</p>
        </SectionTitle>

        <Slider {...settings}>
          {reviews.map((review, index) => (
            <TestimonialCard key={index} $delay={index * 200}>
              <QuoteIcon className="quote-icon">
                <FontAwesomeIcon icon={faQuoteRight} />
              </QuoteIcon>
              <TestimonialContent>{review.comment}</TestimonialContent>
              <ClientInfo>
                <ClientDetails>
                  <h4>{review.name}</h4>
                  <Rating>
                    {[...Array(5)].map((_, i) => (
                      <FontAwesomeIcon
                        key={i}
                        icon={faStar}
                        className="star"
                        style={{ color: i < review.rating ? '#D4AF37' : '#666' }}
                      />
                    ))}
                  </Rating>
                  <p>{formatDate(review.createdAt)}</p>
                </ClientDetails>
              </ClientInfo>
            </TestimonialCard>
          ))}
        </Slider>

        <SectionTitle style={{ marginTop: '4rem' }}>
          <h2>Partagez votre expérience</h2>
          <p>Votre avis compte pour nous</p>
        </SectionTitle>

        {!emailVerification.verified ? (
          <EmailVerification>
            <Form onSubmit={verifyEmail}>
              <FormGroup>
                <Label>Adresse email utilisée lors de votre rendez-vous</Label>
                <EmailInput
                  type="email"
                  value={emailVerification.email}
                  onChange={handleEmailChange}
                  placeholder="Votre email"
                  required
                />
              </FormGroup>
              <VerifyButton
                type="submit"
                disabled={emailVerification.checking}
              >
                {emailVerification.checking ? 'Vérification...' : 'Vérifier'}
              </VerifyButton>
              {emailVerification.error && (
                <Message $success={false}>{emailVerification.error}</Message>
              )}
            </Form>
          </EmailVerification>
        ) : (
          <AddReviewForm onSubmit={handleSubmit}>
            <FormGroup>
              <Label>Nom</Label>
              <Input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </FormGroup>
            <FormGroup>
              <Label>Note</Label>
              <RatingContainer>
                {[1, 2, 3, 4, 5].map((star) => (
                  <StarButton
                    key={star}
                    type="button"
                    onClick={() => handleRatingClick(star)}
                    $isSelected={formData.rating >= star}
                  >
                    <FontAwesomeIcon icon={faStar} />
                  </StarButton>
                ))}
              </RatingContainer>
            </FormGroup>
            <FormGroup>
              <Label>Commentaire</Label>
              <TextArea
                name="comment"
                value={formData.comment}
                onChange={handleChange}
                required
                minLength={10}
                maxLength={500}
                placeholder="Partagez votre expérience avec nous..."
              />
            </FormGroup>
            <SubmitButton
              type="submit"
              disabled={status.submitting}
            >
              {status.submitting ? 'Envoi en cours...' : 'Envoyer mon avis'}
            </SubmitButton>
            {status.message && (
              <Message $success={status.success}>{status.message}</Message>
            )}
          </AddReviewForm>
        )}
      </Container>
    </TestimonialsSection>
  );
}

export default Testimonials;
