export const theme = {
  colors: {
    primary: '#D4AF37',    // Or
    secondary: '#1A1A1A',  // Noir profond
    background: '#1A1A1A', // Noir profond
    text: '#FFFFFF',       // Blanc
    textSecondary: '#CCCCCC' // Gris clair
  },
  fonts: {
    primary: "'Playfair Display', serif",
    secondary: "'Poppins', sans-serif"
  },
