import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSpinner } from '@fortawesome/free-solid-svg-icons';

const VerifyContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: ${props => props.theme.colors.background};
  padding: 2rem;
`;

const VerifyCard = styled.div`
  background: rgba(255, 255, 255, 0.02);
  border: 1px solid rgba(212, 175, 55, 0.1);
  border-radius: 15px;
  padding: 2rem;
  width: 100%;
  max-width: 400px;
  text-align: center;
`;

const Title = styled.h2`
  color: ${props => props.theme.colors.primary};
  margin-bottom: 2rem;
  font-size: 2rem;
`;

const CodeInput = styled.input`
  width: 100%;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(212, 175, 55, 0.2);
  border-radius: 5px;
  color: ${props => props.theme.colors.text};
  font-size: 1.5rem;
  text-align: center;
  letter-spacing: 0.5rem;
  margin-bottom: 1.5rem;
  transition: all 0.3s ease;

  &:focus {
    border-color: ${props => props.theme.colors.primary};
    outline: none;
    box-shadow: 0 0 0 2px rgba(212, 175, 55, 0.1);
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 1rem;
  background: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.background};
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(212, 175, 55, 0.2);
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
  }
`;

const Message = styled.div`
  margin: 1rem 0;
  padding: 1rem;
  border-radius: 5px;
  color: ${props => props.$success ? '#4CAF50' : '#f44336'};
  background: ${props => props.$success ? 'rgba(76, 175, 80, 0.1)' : 'rgba(244, 67, 54, 0.1)'};
  border: 1px solid ${props => props.$success ? '#4CAF50' : '#f44336'};
`;

const LoadingSpinner = styled(FontAwesomeIcon)`
  animation: spin 1s linear infinite;
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const API_URL = 'http://localhost:5000';

function AdminVerify() {
  const navigate = useNavigate();
  const [code, setCode] = useState('');
  const [status, setStatus] = useState({
    loading: false,
    message: '',
    success: false
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (code.length !== 6) {
      setStatus({
        loading: false,
        message: "Le code doit contenir 6 chiffres",
        success: false
      });
      return;
    }

    setStatus({
      loading: true,
      message: 'Vérification en cours...',
      success: false
    });

    try {
      const response = await fetch(`${API_URL}/api/admin/verify-code`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          code,
          email: sessionStorage.getItem('pendingAdminEmail')
        })
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('adminToken', data.token);
        localStorage.setItem('adminTokenExpiry', new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString());
        
        sessionStorage.removeItem('pendingAdminAuth');
        sessionStorage.removeItem('pendingAdminEmail');
        
        setStatus({
          loading: false,
          message: 'Connexion réussie ! Redirection...',
          success: true
        });

        setTimeout(() => {
          navigate('/admin/dashboard', { replace: true });
        }, 1000);
      } else {
        setStatus({
          loading: false,
          message: data.message || "Code invalide",
          success: false
        });
      }
    } catch (error) {
      console.error('Erreur de vérification:', error);
      setStatus({
        loading: false,
        message: "Une erreur s'est produite lors de la vérification",
        success: false
      });
    }
  };

  const handleCodeChange = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, '').slice(0, 6);
    setCode(value);
  };

  return (
    <VerifyContainer>
      <VerifyCard>
        <Title>Vérification</Title>
        <form onSubmit={handleSubmit}>
          <CodeInput
            type="text"
            placeholder="000000"
            value={code}
            onChange={handleCodeChange}
            disabled={status.loading}
            autoFocus
          />
          <SubmitButton type="submit" disabled={status.loading || code.length !== 6}>
            {status.loading ? (
              <LoadingSpinner icon={faSpinner} />
            ) : (
              'Vérifier'
            )}
          </SubmitButton>
        </form>
        {status.message && (
          <Message $success={status.success}>
            {status.message}
          </Message>
        )}
      </VerifyCard>
    </VerifyContainer>
  );
}

export default AdminVerify;
