import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCode, faPalette, faMobile, faRocket } from '@fortawesome/free-solid-svg-icons';

const ServicesSection = styled.section`
  padding: 6rem 2rem;
  background-color: ${props => props.theme.colors.secondary};
  color: white;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 2.5rem;
  color: ${props => props.theme.colors.primary};
  text-align: center;
  margin-bottom: 3rem;
`;

const ServicesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
`;

const ServiceCard = styled.div`
  background-color: rgba(255, 255, 255, 0.05);
  padding: 2rem;
  border-radius: 10px;
  text-align: center;
  transition: transform 0.3s ease;
  
  &:hover {
    transform: translateY(-10px);
  }
`;

const IconWrapper = styled.div`
  font-size: 2.5rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 1.5rem;
`;

const ServiceTitle = styled.h3`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: ${props => props.theme.colors.primary};
`;

const ServiceDescription = styled.p`
  font-family: ${props => props.theme.fonts.secondary};
  line-height: 1.6;
  color: rgba(255, 255, 255, 0.8);
`;

const services = [
  {
    id: 1,
    icon: faCode,
    title: "Développement Web",
    description: "Création de sites web modernes, responsifs et optimisés pour les moteurs de recherche."
  },
  {
    id: 2,
    icon: faPalette,
    title: "Design UI/UX",
    description: "Conception d'interfaces utilisateur intuitives et d'expériences utilisateur exceptionnelles."
  },
  {
    id: 3,
    icon: faMobile,
    title: "Applications Mobiles",
    description: "Développement d'applications mobiles natives et cross-platform pour iOS et Android."
  },
  {
    id: 4,
    icon: faRocket,
    title: "Stratégie Digitale",
    description: "Conseil et accompagnement pour optimiser votre présence numérique et atteindre vos objectifs."
  }
];

const Services = () => {
  return (
    <ServicesSection id="services">
      <Container>
        <Title>Nos Services</Title>
        <ServicesGrid>
          {services.map(service => (
            <ServiceCard key={service.id}>
              <IconWrapper>
                <FontAwesomeIcon icon={service.icon} />
              </IconWrapper>
              <ServiceTitle>{service.title}</ServiceTitle>
              <ServiceDescription>{service.description}</ServiceDescription>
            </ServiceCard>
          ))}
        </ServicesGrid>
      </Container>
    </ServicesSection>
  );
};

export default Services;
