import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const AppointmentSection = styled.section`
  padding: 120px 0;
  background: linear-gradient(to right, rgba(26, 26, 26, 0.95), rgba(26, 26, 26, 0.95)),
              url('/images/appointment-bg.jpg') center/cover;
  color: white;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 4rem;
`;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 3rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 1rem;
  position: relative;
  display: inline-block;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background-color: ${props => props.theme.colors.primary};
  }
`;

const Description = styled.p`
  font-size: 1.1rem;
  max-width: 600px;
  margin: 0 auto;
  color: rgba(255, 255, 255, 0.8);
`;

const Form = styled.form`
  max-width: 800px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;

  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  &.full-width {
    grid-column: 1 / -1;
  }
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: ${props => props.theme.colors.primary};
  font-family: ${props => props.theme.fonts.secondary};
`;

const Input = styled.input`
  width: 100%;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(212, 175, 55, 0.3);
  border-radius: 5px;
  color: white;
  font-size: 1rem;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    background: rgba(255, 255, 255, 0.15);
  }

  &::placeholder {
    color: rgba(255, 255, 255, 0.5);
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(212, 175, 55, 0.3);
  border-radius: 5px;
  color: white;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    background: rgba(255, 255, 255, 0.15);
  }

  option {
    background: #1a1a1a;
    color: white;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(212, 175, 55, 0.3);
  border-radius: 5px;
  color: white;
  font-size: 1rem;
  min-height: 150px;
  resize: vertical;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    background: rgba(255, 255, 255, 0.15);
  }

  &::placeholder {
    color: rgba(255, 255, 255, 0.5);
  }
`;

const SubmitButton = styled.button`
  grid-column: 1 / -1;
  padding: 1rem 3rem;
  background: transparent;
  border: 2px solid ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.primary};
  font-size: 1.1rem;
  font-family: ${props => props.theme.fonts.secondary};
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 1rem;

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: white;
    transform: translateY(-3px);
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
  }
`;

const Message = styled.div`
  grid-column: 1 / -1;
  text-align: center;
  padding: 1rem;
  border-radius: 5px;
  margin-top: 1rem;

  &.success {
    background: rgba(75, 181, 67, 0.2);
    color: #4BB543;
  }

  &.error {
    background: rgba(255, 0, 0, 0.2);
    color: #FF0000;
  }
`;

const Appointment = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    date: '',
    time: '',
    projectType: '',
    message: ''
  });

  const [status, setStatus] = useState({
    submitting: false,
    message: '',
    type: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus({ submitting: true, message: '', type: '' });

    try {
      const response = await axios.post('http://localhost:5000/api/appointment', formData);
      setStatus({
        submitting: false,
        message: 'Votre demande de rendez-vous a été envoyée avec succès. Vous recevrez bientôt un email de confirmation.',
        type: 'success'
      });
      setFormData({
        name: '',
        email: '',
        phone: '',
        date: '',
        time: '',
        projectType: '',
        message: ''
      });
    } catch (error) {
      setStatus({
        submitting: false,
        message: 'Une erreur est survenue. Veuillez réessayer plus tard.',
        type: 'error'
      });
    }
  };

  return (
    <AppointmentSection id="appointment">
      <Container>
        <Header>
          <Title>Prendre Rendez-vous</Title>
          <Description>
            Planifiez une consultation pour discuter de votre projet et transformer 
            votre espace en un lieu unique qui vous ressemble.
          </Description>
        </Header>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label>Nom complet</Label>
            <Input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Votre nom"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>Email</Label>
            <Input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Votre email"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>Téléphone</Label>
            <Input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="Votre numéro de téléphone"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>Type de projet</Label>
            <Select
              name="projectType"
              value={formData.projectType}
              onChange={handleChange}
              required
            >
              <option value="">Sélectionnez un type de projet</option>
              <option value="Résidentiel">Résidentiel</option>
              <option value="Commercial">Commercial</option>
              <option value="Bureau">Bureau</option>
              <option value="Autre">Autre</option>
            </Select>
          </FormGroup>

          <FormGroup>
            <Label>Date souhaitée</Label>
            <Input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>Heure souhaitée</Label>
            <Input
              type="time"
              name="time"
              value={formData.time}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup className="full-width">
            <Label>Message</Label>
            <TextArea
              name="message"
              value={formData.message}
              onChange={handleChange}
              placeholder="Décrivez brièvement votre projet..."
              required
            />
          </FormGroup>

          {status.message && (
            <Message className={status.type}>
              {status.message}
            </Message>
          )}

          <SubmitButton type="submit" disabled={status.submitting}>
            {status.submitting ? 'Envoi en cours...' : 'Envoyer la demande'}
          </SubmitButton>
        </Form>
      </Container>
    </AppointmentSection>
  );
};

export default Appointment;
