import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-scroll';

const shimmer = keyframes`
  0% {
    background-position: -200% center;
  }
  100% {
    background-position: 200% center;
  }
`;

const float = keyframes`
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-20px);
  }
`;

const sparkle = keyframes`
  0%, 100% {
    opacity: 0;
    transform: scale(0);
  }
  50% {
    opacity: 1;
    transform: scale(1);
  }
`;

const HeroSection = styled.section`
  height: 100vh;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('/images/hero-bg.jpg');
  background-size: cover;
  background-position: center;
  color: white;
  overflow: hidden;
`;

const Content = styled.div`
  text-align: center;
  z-index: 2;
  padding: 0 2rem;
`;

const MainTitle = styled.div`
  position: relative;
  margin-bottom: 2rem;
  animation: ${float} 6s ease-in-out infinite;
`;

const Title = styled.h1`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 8rem;
  font-weight: 700;
  letter-spacing: 8px;
  margin: 0;
  background: linear-gradient(
    90deg, 
    #D4AF37 0%, 
    #FFE5B4 25%, 
    #D4AF37 50%, 
    #FFE5B4 75%, 
    #D4AF37 100%
  );
  background-size: 200% auto;
  color: transparent;
  -webkit-background-clip: text;
  background-clip: text;
  animation: ${shimmer} 3s linear infinite;
  text-shadow: 0 0 30px rgba(212, 175, 55, 0.3);

  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    font-size: 4rem;
  }
`;

const Sparkles = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  pointer-events: none;
`;

const Sparkle = styled.div`
  position: absolute;
  width: 8px;
  height: 8px;
  background: #D4AF37;
  border-radius: 50%;
  animation: ${sparkle} 2s infinite;

  &:nth-child(1) { top: 0; left: 20%; animation-delay: 0s; }
  &:nth-child(2) { top: 20%; right: 20%; animation-delay: 0.3s; }
  &:nth-child(3) { bottom: 20%; left: 30%; animation-delay: 0.6s; }
  &:nth-child(4) { bottom: 0; right: 30%; animation-delay: 0.9s; }
`;

const Subtitle = styled.p`
  font-family: ${props => props.theme.fonts.secondary};
  font-size: 1.5rem;
  margin-bottom: 2rem;
  opacity: 0.9;
`;

const CTAButton = styled(Link)`
  display: inline-block;
  padding: 1rem 3rem;
  font-size: 1.2rem;
  font-family: ${props => props.theme.fonts.secondary};
  color: #D4AF37;
  background: transparent;
  border: 2px solid #D4AF37;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;

  &:hover {
    background: #D4AF37;
    color: white;
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  }
`;

const ScrollIndicator = styled.div`
  position: absolute;
  bottom: 2rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  opacity: 0.7;

  &::before {
    content: '';
    width: 2px;
    height: 40px;
    background: white;
    animation: scrollDown 2s infinite;
  }

  @keyframes scrollDown {
    0% {
      transform: scaleY(0);
      transform-origin: top;
    }
    50% {
      transform: scaleY(1);
      transform-origin: top;
    }
    50.1% {
      transform: scaleY(1);
      transform-origin: bottom;
    }
    100% {
      transform: scaleY(0);
      transform-origin: bottom;
    }
  }
`;

const Hero = () => {
  return (
    <HeroSection id="hero">
      <Content>
        <MainTitle>
          <Title>FABULOUS</Title>
          <Sparkles>
            <Sparkle />
            <Sparkle />
            <Sparkle />
            <Sparkle />
          </Sparkles>
        </MainTitle>
        <Subtitle>Créons ensemble votre espace de rêve</Subtitle>
        <CTAButton to="appointment" smooth={true} duration={500}>
          Prendre Rendez-vous
        </CTAButton>
      </Content>
      <ScrollIndicator />
    </HeroSection>
  );
};

export default Hero;
