import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const PortfolioSection = styled.section`
  padding: 120px 0;
  background-color: ${props => props.theme.colors.accent};
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(to right, transparent, ${props => props.theme.colors.primary}, transparent);
  }
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 4rem;
`;

const Subtitle = styled.h3`
  color: ${props => props.theme.colors.primary};
  font-family: ${props => props.theme.fonts.secondary};
  font-size: 1.2rem;
  font-weight: 500;
  margin-bottom: 1rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  animation: ${fadeIn} 0.6s ease-out;
`;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 3rem;
  text-align: center;
  margin-bottom: 1rem;
  color: ${props => props.theme.colors.text};
  animation: ${fadeIn} 0.6s ease-out;
  position: relative;
  display: inline-block;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background-color: ${props => props.theme.colors.primary};
  }
`;

const Description = styled.p`
  max-width: 600px;
  margin: 0 auto;
  font-size: 1.1rem;
  line-height: 1.6;
  color: ${props => props.theme.colors.text};
  opacity: 0.8;
  animation: ${fadeIn} 0.6s ease-out;
`;

const FilterContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 3rem;
  flex-wrap: wrap;
  animation: ${fadeIn} 0.6s ease-out;
`;

const FilterButton = styled.button`
  background: none;
  border: 2px solid ${props => props.active ? props.theme.colors.primary : 'transparent'};
  padding: 0.8rem 2rem;
  font-family: ${props => props.theme.fonts.secondary};
  font-size: 1rem;
  color: ${props => props.active ? props.theme.colors.primary : props.theme.colors.text};
  cursor: pointer;
  transition: all 0.3s ease;
  border-radius: 30px;
  font-weight: 500;

  &:hover {
    color: ${props => props.theme.colors.primary};
    border-color: ${props => props.theme.colors.primary};
    transform: translateY(-2px);
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
  gap: 2rem;
  animation: ${fadeIn} 0.8s ease-out;
`;

const ProjectCard = styled.div`
  position: relative;
  overflow: hidden;
  border-radius: 10px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  cursor: pointer;

  &:hover {
    .overlay {
      opacity: 1;
    }

    img {
      transform: scale(1.1);
    }
  }
`;

const ProjectImage = styled.img`
  width: 100%;
  height: 400px;
  object-fit: cover;
  transition: transform 0.6s ease;
`;

const ProjectOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.2),
    rgba(0, 0, 0, 0.8)
  );
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 2rem;
  opacity: 0;
  transition: opacity 0.3s ease;
  color: white;
`;

const ProjectTitle = styled.h3`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 1.8rem;
  margin-bottom: 1rem;
  transform: translateY(20px);
  transition: transform 0.3s ease;
  ${ProjectCard}:hover & {
    transform: translateY(0);
  }
`;

const ProjectDescription = styled.p`
  font-size: 1rem;
  line-height: 1.6;
  transform: translateY(20px);
  transition: transform 0.3s ease;
  opacity: 0.9;
  ${ProjectCard}:hover & {
    transform: translateY(0);
  }
`;

const ProjectCategory = styled.span`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background-color: ${props => props.theme.colors.primary};
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: 500;
  opacity: 0.9;
`;

const Portfolio = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  const projects = [
    {
      id: 1,
      title: "Appartement Haussmannien",
      description: "Rénovation complète d'un appartement de 120m² alliant style classique et modernité",
      category: "residential",
      image: "/images/project1.jpg"
    },
    {
      id: 2,
      title: "Bureau Open Space",
      description: "Aménagement d'un espace de travail collaboratif de 300m² favorisant la créativité",
      category: "commercial",
      image: "/images/project2.jpg"
    },
    {
      id: 3,
      title: "Restaurant Gastronomique",
      description: "Design intérieur d'un restaurant étoilé mêlant luxe et convivialité",
      category: "commercial",
      image: "/images/project3.jpg"
    },
    {
      id: 4,
      title: "Villa Contemporaine",
      description: "Décoration complète d'une villa de 250m² dans un style contemporain épuré",
      category: "residential",
      image: "/images/project4.jpg"
    },
    {
      id: 5,
      title: "Boutique de Mode",
      description: "Conception d'un espace commercial alliant élégance et fonctionnalité",
      category: "commercial",
      image: "/images/project5.jpg"
    },
    {
      id: 6,
      title: "Loft Industriel",
      description: "Transformation d'un ancien atelier en loft moderne de 180m²",
      category: "residential",
      image: "/images/project6.jpg"
    }
  ];

  const filteredProjects = activeFilter === 'all' 
    ? projects 
    : projects.filter(project => project.category === activeFilter);

  return (
    <PortfolioSection id="portfolio">
      <Container>
        <Header>
          <Subtitle>Nos Réalisations</Subtitle>
          <Title>Projets Sélectionnés</Title>
          <Description>
            Découvrez une sélection de nos plus belles réalisations, 
            où chaque projet raconte une histoire unique et reflète 
            notre passion pour le design d'intérieur.
          </Description>
        </Header>

        <FilterContainer>
          <FilterButton 
            active={activeFilter === 'all'} 
            onClick={() => setActiveFilter('all')}
          >
            Tous les Projets
          </FilterButton>
          <FilterButton 
            active={activeFilter === 'residential'} 
            onClick={() => setActiveFilter('residential')}
          >
            Résidentiel
          </FilterButton>
          <FilterButton 
            active={activeFilter === 'commercial'} 
            onClick={() => setActiveFilter('commercial')}
          >
            Commercial
          </FilterButton>
        </FilterContainer>

        <Grid>
          {filteredProjects.map(project => (
            <ProjectCard key={project.id}>
              <ProjectImage src={project.image} alt={project.title} />
              <ProjectCategory>{project.category === 'residential' ? 'Résidentiel' : 'Commercial'}</ProjectCategory>
              <ProjectOverlay className="overlay">
                <ProjectTitle>{project.title}</ProjectTitle>
                <ProjectDescription>{project.description}</ProjectDescription>
              </ProjectOverlay>
            </ProjectCard>
          ))}
        </Grid>
      </Container>
    </PortfolioSection>
  );
};

export default Portfolio;
