import React, { useState } from 'react';
import styled from 'styled-components';
import { TextField } from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import frLocale from 'date-fns/locale/fr';

const AppointmentSection = styled.section`
  padding: 100px 0;
  background-color: ${props => props.theme.colors.accent};
`;

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 3rem;
  color: ${props => props.theme.colors.text};
`;

const Form = styled.form`
  display: grid;
  gap: 2rem;
  background-color: ${props => props.theme.colors.background};
  padding: 2rem;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const FormGroup = styled.div`
  display: grid;
  gap: 1rem;
  grid-template-columns: ${props => props.fullWidth ? '1fr' : '1fr 1fr'};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
  }
`;

const StyledTextField = styled(TextField)`
  && {
    .MuiOutlinedInput-root {
      &:hover fieldset {
        border-color: ${props => props.theme.colors.primary};
      }
    }
    .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline {
      border-color: ${props => props.theme.colors.primary};
    }
  }
`;

const SubmitButton = styled.button`
  background-color: ${props => props.theme.colors.primary};
  color: white;
  border: none;
  padding: 1rem 2rem;
  font-family: ${props => props.theme.fonts.secondary};
  font-size: 1.1rem;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${props => props.theme.colors.primary}dd;
  }
`;

const TimeSlotGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
  gap: 1rem;
  margin-top: 1rem;
`;

const TimeSlot = styled.button`
  padding: 0.5rem;
  border: 2px solid ${props => props.selected ? props.theme.colors.primary : props.theme.colors.text};
  background-color: ${props => props.selected ? props.theme.colors.primary : 'transparent'};
  color: ${props => props.selected ? 'white' : props.theme.colors.text};
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    border-color: ${props => props.theme.colors.primary};
    color: ${props => props.selected ? 'white' : props.theme.colors.primary};
  }
`;

const Appointment = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    date: null,
    timeSlot: '',
    message: ''
  });

  const timeSlots = [
    '09:00', '10:00', '11:00', '14:00', '15:00', '16:00'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDateChange = (date) => {
    setFormData(prev => ({
      ...prev,
      date
    }));
  };

  const handleTimeSlotSelect = (slot) => {
    setFormData(prev => ({
      ...prev,
      timeSlot: slot
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici, vous pouvez ajouter la logique pour envoyer les données du formulaire
    console.log(formData);
  };

  return (
    <AppointmentSection id="appointment">
      <Container>
        <Title>Prendre Rendez-vous</Title>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <StyledTextField
              name="firstName"
              label="Prénom"
              variant="outlined"
              value={formData.firstName}
              onChange={handleChange}
              required
            />
            <StyledTextField
              name="lastName"
              label="Nom"
              variant="outlined"
              value={formData.lastName}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <StyledTextField
              name="email"
              label="Email"
              type="email"
              variant="outlined"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <StyledTextField
              name="phone"
              label="Téléphone"
              variant="outlined"
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <LocalizationProvider dateAdapter={AdapterDateFns} adapterLocale={frLocale}>
              <DatePicker
                label="Date du rendez-vous"
                value={formData.date}
                onChange={handleDateChange}
                renderInput={(params) => <StyledTextField {...params} />}
                disablePast
              />
            </LocalizationProvider>
          </FormGroup>

          <TimeSlotGrid>
            {timeSlots.map(slot => (
              <TimeSlot
                key={slot}
                selected={formData.timeSlot === slot}
                onClick={() => handleTimeSlotSelect(slot)}
                type="button"
              >
                {slot}
              </TimeSlot>
            ))}
          </TimeSlotGrid>

          <FormGroup fullWidth>
            <StyledTextField
              name="message"
              label="Message (optionnel)"
              multiline
              rows={4}
              variant="outlined"
              value={formData.message}
              onChange={handleChange}
            />
          </FormGroup>

          <SubmitButton type="submit">
            Envoyer la demande
          </SubmitButton>
        </Form>
      </Container>
    </AppointmentSection>
  );
};

export default Appointment;
