import React from 'react';
import styled from 'styled-components';

const AboutSection = styled.section`
  padding: 100px 0;
  background-color: ${props => props.theme.colors.background};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const ImageContainer = styled.div`
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    width: 100%;
    height: 100%;
    border: 2px solid ${props => props.theme.colors.primary};
    z-index: 1;
  }
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  position: relative;
  z-index: 2;
`;

const Content = styled.div`
  h2 {
    font-family: ${props => props.theme.fonts.primary};
    font-size: 2.5rem;
    color: ${props => props.theme.colors.text};
    margin-bottom: 1.5rem;
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: ${props => props.theme.colors.text};
    margin-bottom: 1.5rem;
  }
`;

const Signature = styled.div`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 1.5rem;
  color: ${props => props.theme.colors.primary};
  margin-top: 2rem;
`;

const About = () => {
  return (
    <AboutSection id="about">
      <Container>
        <ImageContainer>
          <Image src="/images/designer-portrait.jpg" alt="Fabulous Designer" />
        </ImageContainer>
        <Content>
          <h2>À propos de Fabulous</h2>
          <p>
            Passionnée par l'art et le design depuis mon plus jeune âge, j'ai consacré ma carrière 
            à transformer des espaces ordinaires en lieux extraordinaires. Avec plus de 10 ans 
            d'expérience dans l'architecture d'intérieur, je crois fermement que chaque espace 
            raconte une histoire unique.
          </p>
          <p>
            Ma philosophie de design repose sur l'harmonie entre l'esthétique et la fonctionnalité. 
            Je m'efforce de créer des intérieurs qui non seulement impressionnent visuellement, 
            mais qui améliorent également la qualité de vie de mes clients.
          </p>
          <p>
            Chaque projet est une nouvelle aventure, une opportunité de repousser les limites 
            de la créativité tout en respectant les besoins et les désirs spécifiques de mes clients.
          </p>
          <Signature>Fabulous</Signature>
        </Content>
      </Container>
    </AboutSection>
  );
};

export default About;
