import React from 'react';
import styled from 'styled-components';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const TestimonialsSection = styled.section`
  padding: 100px 0;
  background-color: ${props => props.theme.colors.background};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const Title = styled.h2`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 3rem;
  color: ${props => props.theme.colors.text};
`;

const TestimonialCard = styled.div`
  padding: 2rem;
  margin: 1rem;
  background-color: ${props => props.theme.colors.accent};
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const TestimonialContent = styled.p`
  font-size: 1.1rem;
  line-height: 1.8;
  color: ${props => props.theme.colors.text};
  margin-bottom: 2rem;
  font-style: italic;
`;

const ClientInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const ClientImage = styled.img`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  object-fit: cover;
`;

const ClientDetails = styled.div``;

const ClientName = styled.h4`
  font-family: ${props => props.theme.fonts.primary};
  font-size: 1.2rem;
  color: ${props => props.theme.colors.primary};
  margin: 0;
`;

const ClientLocation = styled.p`
  font-size: 0.9rem;
  color: ${props => props.theme.colors.text};
  margin: 0;
`;

const StyledSlider = styled(Slider)`
  .slick-dots li button:before {
    color: ${props => props.theme.colors.primary};
  }

  .slick-prev:before,
  .slick-next:before {
    color: ${props => props.theme.colors.primary};
  }
`;

const Testimonials = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  const testimonials = [
    {
      content: "Travailler avec Fabulous a été une expérience extraordinaire. Elle a su capturer notre vision tout en y ajoutant sa touche créative unique. Le résultat final dépasse toutes nos attentes !",
      name: "Sophie Martin",
      location: "Paris",
      image: "/images/client1.jpg"
    },
    {
      content: "Une professionnelle exceptionnelle qui a transformé notre appartement en un véritable havre de paix. Son attention aux détails et sa créativité sont remarquables.",
      name: "Pierre Dubois",
      location: "Lyon",
      image: "/images/client2.jpg"
    },
    {
      content: "Fabulous a su moderniser notre espace commercial tout en conservant son caractère authentique. Un travail d'une qualité exceptionnelle !",
      name: "Marie Laurent",
      location: "Bordeaux",
      image: "/images/client3.jpg"
    }
  ];

  return (
    <TestimonialsSection id="testimonials">
      <Container>
        <Title>Ce que disent nos clients</Title>
        <StyledSlider {...settings}>
          {testimonials.map((testimonial, index) => (
            <TestimonialCard key={index}>
              <TestimonialContent>
                "{testimonial.content}"
              </TestimonialContent>
              <ClientInfo>
                <ClientImage src={testimonial.image} alt={testimonial.name} />
                <ClientDetails>
                  <ClientName>{testimonial.name}</ClientName>
                  <ClientLocation>{testimonial.location}</ClientLocation>
                </ClientDetails>
              </ClientInfo>
            </TestimonialCard>
          ))}
        </StyledSlider>
      </Container>
    </TestimonialsSection>
  );
};

export default Testimonials;
